import React, { Component } from 'react';
import { Input, Button, IconButton } from '@material-ui/core';
import GitHubIcon from '@material-ui/icons/GitHub';
import "./Home.css"
import home from '../'

class Home extends Component {
  	constructor (props) {
		super(props)
		this.state = {
			url: ''
		}
	}

	handleChange = (e) => this.setState({ url: e.target.value })

	join = () => {
		if (this.state.url !== "") {
			var url = this.state.url.split("/")
			window.location.href = `/${url[url.length-1]}`
		} else {
			var url = Math.random().toString(36).substring(2, 7)
			window.location.href = `/${url}`
		}
	}

	render() {
		return (
			<div className="container2">
				<nav>
					<div className="left-item">
						<img src="../images/logo.png" />
					</div>
					<div className="right-item">
						<div className="date-time">
							<span id="date" />
							<span>/</span>
							<span id="month" />
							<span>•</span>
							<span id="time" />
						</div>
						<div>
							<i className="far fa-question-circle" />
						</div>
						<div>
							<i className="far fa-comment-alt" />
						</div>
						<div>
							<i className="fa fa-cog" />
						</div>
						<div>
							<i className="fa fa-ellipsis-v" />
						</div>
						<div className="login-icon">
							<span id="log-icon">
							<i className="far fa-user" />
							</span>
						</div>
					</div>
      			</nav>
				<div>
					<h1 style={{ fontSize: "45px" }}>Video Meeting</h1>
					<p style={{ fontWeight: "200" }}>Video conference website that lets you stay in touch with all your friends.</p>
				</div>

				<div style={{
					background: "white", width: "30%", height: "auto", padding: "20px", minWidth: "400px",
					textAlign: "center", margin: "auto", marginTop: "100px"
				}}>
					<p style={{ margin: 0, fontWeight: "bold", paddingRight: "50px" }}>Start or join a meeting</p>
					<Input placeholder="URL" onChange={e => this.handleChange(e)} />
					<Button className='Go-button' onClick={this.join} style={{ margin: "20px", backgroundColor: "black"}}>Go</Button>
				</div>
			</div>
		)
	}
}

export default Home;
